

/* --------------------------------------------------------------------------- */
/* 0. (facultatif) : accepter les littéraux MM/DD/YYYY                         */
SET datestyle TO 'MDY';
/* --------------------------------------------------------------------------- */

/* 1. Générer statement_period pour toutes les lignes encore NULL              */
/*    - borne basse  = generation_date                                         */
/*    - borne haute  = generation_date + N jours (1 ≤ N ≤ 2030-01-01 – date)   */
WITH limites AS (
    SELECT  statement_id,
            generation_date::date                                 AS d0,
            GREATEST(1, (DATE '2030-01-01' - generation_date))    AS max_days
    FROM    accountstatement
    WHERE   statement_period IS NULL
)
UPDATE accountstatement AS a
SET    statement_period =
       daterange(
           l.d0,                                                     -- borne basse : date
           ( l.d0                                                    -- borne haute :
             + ( (1 + floor(random() * l.max_days))::int )
               * interval '1 day'
           )::date,                                                  --  ▶ CAST en date ◀
           '[)' )                                                    -- intervalle [d0, haute)
FROM   limites l
WHERE  a.statement_id = l.statement_id;
